import React, { useEffect, useRef } from 'react'
import Chartjs from 'chart.js'
import { chartOptions } from '../chartConfigs/chartConfig';

function HistoryChart({data})
{
  const chartRef=useRef();   //  chartref.current will be used to refer to the canvas
  const { day, week, year, detail } = data;
  useEffect(() => {
    if(chartRef && chartRef.current && detail)   // if chartref exists and has a legit value
    {
        const chartInstance = new Chartjs(chartRef.current, {
        type: 'line',
        data: {
            labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
            datasets: [{
                label: `${detail.name} pri`,
                data: day,
                backgroundColor: "rgba(174, 305, 194, 0.5)",
                borderColor: "rgba(174, 305, 194, 0.4",
                pointRadius: 0,
                
            }]
        },
        options: chartOptions,
      });
    }    
  },[])
  

  return (
    <div className="bg-white border mt-2 rounded p-3">
      <div>
        
      </div>
      <div>
        <canvas ref={chartRef} id='myChart' width={250} height={250}></canvas>
      </div>
    </div>
  )
}

export default HistoryChart
