import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import CoinData from '../components/CoinData';
import HistoryChart from '../components/HistoryChart';
import coinGecko from '../apis/coinGecko';

function CoinDetailPage()
{
  var coinid=useParams().id;
  const [coinData,setCoinData]=useState({});
  useEffect(()=>{
    async function fetchdata()
    {
      const {day,week,year}=Promise.all([coinGecko.get(`/coins/${coinid}/market_chart/`,{
        params: {
            vs_currency: "inr",
            days: "1"
        }
    }),])
        const resultsDay = await coinGecko.get(`/coins/${coinid}/market_chart/`,{
            params: {
                vs_currency: "inr",
                days: "1"
            }
        })
        const resultsWeek = await coinGecko.get(`/coins/${coinid}/market_chart/`,{
            params: {
                vs_currency: "inr",
                days: "7" 
            }
        })
        const resultsYear = await coinGecko.get(`/coins/${coinid}/market_chart/`,{
            params: {
                vs_currency: "inr",
                days: "365"
            }
        })
        setCoinData({day:resultsDay.data.prices , week:resultsWeek.data.prices , year:resultsYear.data.prices});
        console.log(coinData);
    }
    fetchdata();
},[])
  return (
    <div>
        <HistoryChart></HistoryChart>
        <CoinData></CoinData>
    </div>
  )
}

export default CoinDetailPage
